import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { singleArticle } from "../utils/api";
import { Card } from "react-bootstrap";
import { increaseVotes } from "../utils/api"

const SingleArticle = ({ selectedArticle, setSelectArticle }) => {
  const [article, setArticle] = useState([]);
  const [err, setErr] = useState(null);
  const { article_id } = useParams();
  const clickHandler = (inc) => {
    setVotes((currVotes) => {
      const newVotes = currVotes + inc;
      return newVotes;
    });
    increaseVotes(article_id, inc)
  };

  const { votes, setVotes } = useState(0);

  useEffect(() => {
    singleArticle(article_id)
      .then((articleFromApi) => {
        setArticle(articleFromApi);
        setErr(null);
        setVotes(articleFromApi.votes)
      })
      .catch(() => {
        setErr("Article not found :(");
      });
  }, [article_id]);

  if (err)
    return (
      <div>
            <h2>Article not found</h2>
      </div>
    );
      console.log(selectedArticle)
  return (
    <div key={selectedArticle.article_id}>
          <h2>{selectedArticle.title}</h2>
          <p>By {selectedArticle.author}</p>
          <h3>{selectedArticle.body}</h3>
          <p>votes: {votes}</p>
          <button
            onClick={() => {
              clickHandler(1);
            }}>
              üëç
            </button>
            <button
            onClick={() => {
              clickHandler(-1);
            }}>
              üëé
            </button>
          <Card.Footer>
            <small className="text-muted">Topic: {article.topic}</small>
          </Card.Footer>
    </div>
  );
};

export default SingleArticle;
