import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { singleArticle } from "../utils/api";
import { Card } from "react-bootstrap";
import { increaseVotes } from "../utils/api"

const SingleArticle = () => {
  const [article, setArticle] = useState([]);
  const [err, setErr] = useState(null);
  const { article_id } = useParams();
  const clickHandler = (inc) => {
    setVotes((currVotes) => {
      const newVotes = currVotes + inc;
      return newVotes;
    });
    increaseVotes(article_id, inc)
  };

  const { votes, setVotes } = useState(0);

  useEffect(() => {
    singleArticle(article_id)
      .then((articleFromApi) => {
        setArticle(articleFromApi);
        setErr(null);
        setVotes(article.votes)
      })
      .catch(() => {
        setErr("Article not found :(");
      });
  }, [article_id]);

  if (err)
    return (
      <div>
        <Card>
          <Card.Body>
            <Card.Title>Article not found</Card.Title>
          </Card.Body>
        </Card>
      </div>
    );

  return (
    <div>
      <Card key={article.article_id}>
        <Card.Body>
          <Card.Title>{article.title}</Card.Title>
          <Card.Text>By {article.author}</Card.Text>
          <Card.Text>{article.body}</Card.Text>
          <Card.Text>votes: {votes}</Card.Text>
          <button
            onClick={() => {
              clickHandler(1);
            }}>
              üëç
            </button>
            <button
            onClick={() => {
              clickHandler(-1);
            }}>
              üëé
            </button>
          <Card.Footer>
            <small className="text-muted">Topic: {article.topic}</small>
          </Card.Footer>
        </Card.Body>
      </Card>
    </div>
  );
};

export default SingleArticle;
